fieldset.script:
$(function(){ $("#certificate-upload").fileinput({ theme: 'fas', uploadUrl:'/my/productions/uploaded?type=cert&id=', showClose: true, showUpload: true, showCaption: true, browseClass: "btn btn-primary btn-lg", fileType: "any", previewFileIcon: '', overwriteInitial: false, initialPreviewAsData: true, initialPreview: [
"",
], });
$("#agreement-upload").fileinput({ theme: 'fas', uploadUrl:'/my/productions/uploaded?type=agreement&id=', showClose: true, showUpload: true, showCaption: true, browseClass: "btn btn-primary btn-lg", fileType: "any", previewFileIcon: '', overwriteInitial: false, initialPreviewAsData: true, initialPreview: [
"",
], }); })

$(function(){ $("#certificate-upload").fileinput({ theme: 'fas', uploadUrl:'/my/productions/uploaded?type=cert', showClose: true, showUpload: true, showCaption: true, browseClass: "btn btn-primary btn-lg", fileType: "any", previewFileIcon: '', overwriteInitial: false, initialPreviewAsData: false, }).on('fileuploaded', function(event, data, i, fileId) { let _picId = data.response['fileId'] let _val = $('#certificate_uploaded_image').val() $('#certificate_uploaded_image').val(`${_val},${_picId}`) });
$("#agreement-upload").fileinput({ theme: 'fas', uploadUrl:'/my/productions/uploaded?type=agreement', showClose: true, showUpload: true, showCaption: true, browseClass: "btn btn-primary btn-lg", fileType: "any", previewFileIcon: '', overwriteInitial: false, initialPreviewAsData: false }).on('fileuploaded', function(event, data, i, fileId) { let _picId = data.response['fileId'] let _val = $('#agreement_uploaded_image').val() $('#agreement_uploaded_image').val(`${_val},${_picId}`) });
})
